    const message = object.sentmessages.message;
    console.log(fullName);
    return (
      <div id='message'>{message}</div>
    );
  }
  return (
    <div>
      <div id='dm-head'>
        <div id='dm-type'>
          {props.name}
        </div>
      </div>
      <div id='dm-main'>
        {currMessages}
        <div id='dm-adder'>
          <input
            id='add-dm'
            type='text'
            onInput={(event)=>setAddedDM(event.target.value)}
            value={addedDM}
            placeholder='Send Message'
          />
        </div>
        <button id='dm-button'
          onClick={()=>addDM(addedDM)}>
          Send
        </button>
      </div>
    </div>
  );
}

export default Dm;
